/*Table structure for table `cm_party` */

DROP TABLE IF EXISTS `cm_party`;

CREATE TABLE `cm_party` (
  `PARTY_ID` bigint(12) NOT NULL COMMENT '参与人编号\n\n表示一个参与人的唯一标识，由系统生成的全局唯一的序列号。',
